import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useWeb3 } from "../hook/useWeb3";

const styles = {
  container: {
    maxWidth: '420px',
    margin: '40px auto',
    padding: '32px',
    background: '#fff',
    borderRadius: '14px',
    boxShadow: '0 4px 24px rgba(0,0,0,0.09)',
    fontFamily: 'Segoe UI, Arial, sans-serif',
  },
  heading: {
    color: '#1a237e',
    marginBottom: '24px',
    fontWeight: 700,
    fontSize: '1.5rem',
    letterSpacing: '0.5px',
    textAlign: 'center',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '18px',
    marginBottom: '28px',
  },
  label: {
    fontWeight: 500,
    color: '#333',
    marginBottom: '6px',
  },
  input: {
    padding: '10px 12px',
    border: '1px solid #bdbdbd',
    borderRadius: '6px',
    fontSize: '1rem',
    outline: 'none',
    transition: 'border 0.2s',
  },
  button: {
    background: 'linear-gradient(90deg,#3949ab 0,#1976d2 100%)',
    color: '#fff',
    border: 'none',
    borderRadius: '6px',
    padding: '12px',
    fontWeight: 600,
    fontSize: '1rem',
    cursor: 'pointer',
    marginTop: '8px',
    transition: 'background 0.2s',
  },
  section: {
    background: '#f5f7fa',
    borderRadius: '8px',
    padding: '16px',
    marginBottom: '18px',
    boxShadow: '0 1px 4px rgba(60,60,60,0.04)',
  },
  sectionTitle: {
    color: '#1976d2',
    fontWeight: 600,
    marginBottom: '8px',
    fontSize: '1.1rem',
  },
  info: {
    color: '#222',
    fontSize: '1rem',
    margin: '4px 0',
    wordBreak: 'break-all',
  },
  link: {
    display: 'inline-block',
    marginTop: '18px',
    color: '#1976d2',
    textDecoration: 'none',
    fontWeight: 500,
    fontSize: '1rem',
    transition: 'color 0.2s',
  },
};

function AdditionPage() {
  const {
    web3,
    accounts,
    contracts,
    loading,
    error,
    isReady,
    currentAccount,
  } = useWeb3();

  const [a, setA] = useState('');
  const [b, setB] = useState('');
  const [result, setResult] = useState('');
  const [latestBlock, setLatestBlock] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log("Contracts in AdditionPage:", contracts);

    try {
      if (!contracts.AdditionContract) {
        alert('Le contrat AdditionContract n’est pas chargé.');
        return;
      }

      console.log("Contracts object:", contracts);
      console.log("AdditionContract instance:", contracts.AdditionContract);

      const sum = await contracts.AdditionContract.contract.methods
        .addition2(parseInt(a), parseInt(b))
        .call();

      setResult(sum);

      const block = await web3.eth.getBlock('latest');
      setLatestBlock(block.number);
    } catch (err) {
      console.error('Erreur lors de l’exécution:', err);
      alert('Erreur lors de l’exécution: ' + (err.message || err));
    }
  };

  if (loading) return <div style={styles.container}>⏳ Connexion à la blockchain...</div>;
  if (error) return <div style={styles.container}>❌ Erreur : {error}</div>;

  return (
    <div style={styles.container}>
      <div style={styles.heading}>Exercice 1 : Addition de deux variables</div>

      <form style={styles.form} onSubmit={handleSubmit}>
        <div>
          <label style={styles.label}>
            A :
            <input
              type="number"
              value={a}
              onChange={(e) => setA(e.target.value)}
              required
              style={styles.input}
              min="0"
            />
          </label>
        </div>
        <div>
          <label style={styles.label}>
            B :
            <input
              type="number"
              value={b}
              onChange={(e) => setB(e.target.value)}
              required
              style={styles.input}
              min="0"
            />
          </label>
        </div>
        <button type="submit" style={styles.button}>
          Calculer la somme
        </button>
      </form>

      {result && (
        <div style={styles.section}>
          <div style={styles.sectionTitle}>Résultat :</div>
          <div style={styles.info}>{result}</div>
        </div>
      )}

      <div style={styles.section}>
        <div style={styles.sectionTitle}>Infos Blockchain</div>
        <div style={styles.info}>
          <strong>Compte connecté :</strong> {currentAccount || 'Non connecté'}
        </div>
        <div style={styles.info}>
          <strong>Dernier bloc :</strong> {latestBlock || '...'}
        </div>
      </div>

      <Link to="/" style={styles.link}>
        ← Retour au sommaire
      </Link>
    </div>
  );
}

export default AdditionPage;
